import React, { useState, useEffect } from 'react';
import 'bootstrap';

import List from 'components/List';

function App() {
  const URL = 'http://localhost:1337/articles'
  const options = {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' }
  }
  const [listArticles, setListArticles] = React.useState([])

  const dataArticles = () => {
    fetch(URL, options)
      .then(res => res.json())
      .then(data => console.log('data => ', data))
      // .then(data => setListArticles(data))
      // .then(data => console.log('listArticles => ', listArticles))
  }

  const allArticles = () => {
    listArticles.map(item => {
      <List key={item.id} itemArticle={item} />
    })
  }

  useEffect(
    () => {
      const data = dataArticles()
      setListArticles(dataArticles)
      console.log('listArticles => ', listArticles);
    }, [dataArticles]
  )

  return (
    <div className="App">
      <div className="container">
        <article>
          {/* <ul>{allArticles()}</ul> */}
        </article>
      </div>
    </div>
  );
}

export default App;
